/*Table structure for table `order_item` */

DROP TABLE IF EXISTS `order_item`;

CREATE TABLE `order_item` (
  `order_id` bigint(20) NOT NULL COMMENT '订单id',
  `business_id` bigint(20) DEFAULT NULL COMMENT '商户id',
  `good_id` bigint(20) DEFAULT NULL COMMENT '商品id',
  `good_name` varchar(64) DEFAULT NULL COMMENT '商品名称',
  `good_image` varchar(255) DEFAULT NULL COMMENT '商品展示图',
  `good_price` decimal(10,2) DEFAULT NULL COMMENT '商品价格',
  `good_number` int(11) DEFAULT NULL COMMENT '商品数量',
  `total` int(11) DEFAULT NULL COMMENT '商品总价',
  `status` char(1) DEFAULT NULL COMMENT '订单状态 1待付款，2待派送，3已派送，4已完成，5已取消',
  `update_by` bigint(20) DEFAULT NULL COMMENT '修改人',
  `create_by` bigint(20) DEFAULT NULL COMMENT '创建人',
  `update_time` datetime DEFAULT NULL COMMENT '修改时间',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`order_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

