/*Table structure for table `orders` */

DROP TABLE IF EXISTS `orders`;

CREATE TABLE `orders` (
  `order_id` bigint(20) NOT NULL COMMENT '订单id',
  `user_id` bigint(20) NOT NULL COMMENT '用户id',
  `business_id` bigint(20) NOT NULL COMMENT '商家id',
  `consignee` varchar(64) DEFAULT NULL COMMENT '收货人',
  `phone` bigint(11) DEFAULT NULL COMMENT '手机',
  `province_name` varchar(64) DEFAULT NULL COMMENT '省',
  `city_name` varchar(64) DEFAULT NULL COMMENT '市',
  `county_name` varchar(64) DEFAULT NULL COMMENT '县、区',
  `detail_address` varchar(255) DEFAULT NULL COMMENT '详细地址',
  `amount` bigint(20) DEFAULT NULL COMMENT '数量',
  `price` decimal(10,2) NOT NULL COMMENT '价格',
  `status` char(1) NOT NULL DEFAULT '1' COMMENT '订单状态',
  `time_expire` datetime NOT NULL COMMENT '订单失效时间',
  `goods_detail` varchar(255) DEFAULT NULL COMMENT '商品详细',
  `pay_method` char(1) DEFAULT '1' COMMENT '支付方式',
  `update_by` varchar(64) NOT NULL COMMENT '修改人',
  `create_by` varchar(64) NOT NULL COMMENT '创建人',
  `update_time` datetime NOT NULL COMMENT '修改时间',
  `create_time` datetime NOT NULL COMMENT '创建时间',
  PRIMARY KEY (`order_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='订单表';

